const URL = window.location.href;
var section;
if (URL.indexOf("/icon/") !== -1){section = "icon";}
else if (URL.indexOf("/video/") !== -1){section = "video";}
else if (URL.indexOf("/image/") !== -1){section = "image";}
else if (URL.indexOf("/vector/") !== -1){section = "vector";}
else{ section = false}

if (section !== false){

    $(window).on('load', function () {
        // getting the saved value
        var obj = JSON.parse(localStorage.getItem(section));

        // foreach apply the liked action
        var saved = {};
        saved[section] = []

        if (obj === null) {
            return saved
        }
        obj.forEach(o => {
            var sitename = o.sitename;
            var liked = o.liked;
            var target = document.querySelector(`[data-site="${sitename}"]`);
            target.dataset.liked = liked;
            
            let L = {};
            L.sitename = sitename;
            L.liked = target.dataset.liked;
            
            start = document.getElementById("sitelist")
            if (L.liked === "true") {
                start.prepend(target)
            }
            if (L.liked === "false") {
                start.append(target)
            }
            saved[section].push(L)
            
        });
        
        return
    })
    
    function like(el, e, status) {
        
        e.preventDefault()
        
        var saved = JSON.parse(localStorage.getItem(section));
        
        if (saved === null) {
            saved = []
        }
        console.table(saved[section])
        var target = el.parentElement.parentElement;
        var sitename = target.dataset.site;
        var liked = target.dataset.liked;
        console.log(liked)
        
        var found = false
        saved.forEach((l) => {
            if (l.sitename == sitename) {
                found = true
            }
        })
        
        if (liked != status) {
            target.dataset.liked = status;
            let L = {};
            L.sitename = sitename;
            L.liked = target.dataset.liked;
            var saved = arrayRemove(saved, L);
            localStorage.setItem(section, JSON.stringify(saved));
        }
        if (liked == status) {
            obj = JSON.parse(localStorage.getItem(section))
           
            let ret = [];
            obj.forEach((o) => {
                if (o.sitename != sitename){
                    ret.push(o)
                } else {
                    target.dataset.liked = "none";
                }
            })
            localStorage.setItem(section, JSON.stringify(ret));
        }
        
        if (found !== true) {
            
            let L = {};
            L.sitename = sitename;
            L.liked = target.dataset.liked;
            
            saved.push(L);
            localStorage.setItem(section, JSON.stringify(saved));
        }
        
        start = document.getElementById("sitelist")
        
        if (status == "true") {
            start.prepend(target);
        }
        if (status == "false") {
            start.append(target);
        }
        
    }
    
}
    
    
function arrayRemove(arr, value) { 
    
    return arr.filter(function(ele){ 
        return ele != value; 
    })
}